package com.cg.daytwo;

public class Arit_opera {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Arithmetic Operations
				int a = 15 , b = 10;
				float c;
				
				// ADD
				c=a+b;
				System.out.println("Adds : "+c);
				
				//SUB
				c=a-b;
				System.out.println("Subs : "+c);
				
				//Multiply
				c=a*b;
				System.out.println("Multi : "+c);
				
				//Division
				c=a/b;
				System.out.println("Divi : "+c);
				
				//Module
				c=a%b;
				System.out.println("Module : "+c);
			}

		}